import React, {Component} from "react";
import { Link } from "react-router-dom";
import Header from './Header'

class Form extends Component {
    state = {
        name:'',
        upc:'',
        available_on:'', 
        property_name:'',
        property_value:''
    }

    // stripHtmlEntities(str) {
    //     return String(str)
    //         .replace(/</g, "&lt;")
    //         .replace(/>/g, "&gt;");
    // }

    onChange = (e) => {
        const input = e.target;
        const name = input.name;
        const value = input.type ===  'checkbox' ? input.checked : input.value;
        this.setState({ [name]: value });
    }

    onSubmit(event) {
        event.preventDefault();
        const url = "/api/v1/products";
        const { name, upc, available_on, property_name, property_value } = this.state;

        if (name.length == 0 || upc.length == 0 || available_on.length == 0 || property_name.length == 0 || property_value.length == 0)
          return;

        const body = {
          name,
          upc,
          available_on,
          property_name,
          property_value
        };

        const token = document.querySelector('meta[name="csrf-token"]').content;
        fetch(url, {
          method: "POST",
          headers: {
            "X-CSRF-Token": token,
            "Content-Type": "application/json"
          },
          body: JSON.stringify(body)
        })
          .then(response => {
            if (response.ok) {
              return response.json();
            }
            throw new Error("Network response was not ok.");
          })
        //   .then(response => this.props.history.push(`/product/${response.id}`))
          .catch(error => console.log(error.message));
    }



    render() {
        return (
            <div className='product-form'>
                <Header 
                    title='create a product'
                />

                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label htmlFor="productName">Product name</label>
                        <input
                        type="text"
                        name="name"
                        id="productName"
                        className="form-control"
                        required
                        onChange={this.onChange}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="productUpc">UPC</label>
                        <input
                        type="text"
                        name="upc"
                        id="productUpc"
                        className="form-control"
                        required
                        onChange={this.onChange}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="productAvailable">Available On</label>
                        <input
                            type='date'
                            className="form-control"
                            id="productAvailable"
                            name="available"
                            required
                            onChange={this.onChange}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="productPropertyName">Property Name</label>
                        <input
                        type="text"
                        name="property_name"
                        id="productPropertyName"
                        className="form-control"
                        required
                        onChange={this.onChange}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="productPropertyValue">Property Value</label>
                        <input
                        type="text"
                        name="property_value"
                        id="productPropertyValue"
                        className="form-control"
                        required
                        onChange={this.onChange}
                        />
                    </div>
                    <button type="submit" className="btn custom-button mt-3">
                        Create Product
                    </button>
                </form> 

                <Link
                    to="/listing"
                    className="btn btn-lg custom-button"
                    role="button"
                >
                View Products
                </Link>
            </div>
        )
    }
}

export default Form;
